import {GET_RECIPES, 
     } from "./actionsTypes"
import axios from 'axios'
import { dispatch } from 'redux';
    


export const getRecipes =async (dispatch) => {
    let response = await axios("http://localhost:3001/recipe")
    return dispatch({
        type: GET_RECIPES,
        payload: response.data,
    })
}

export const getDiets = async() => {
    let response = await axios("http://localhost:3001/diets")
    return dispatch({
        type: GET_DIETS,
        payload: response.data,
    })
}

export const searchRecipes = (name) => {
    return async (dispatch) =>{
    let response = await axios(`http://localhost:3001/recipe?name=${name}`)
    return dispatch({
        type: SEARCH_RECIPES,
        payload: response.data,
    })
}
}

export const getDetail = (id) => {
    return async (dispatch) =>{
    let response = await axios(`http://localhost:3001/recipes/${id}`)
    return dispatch({
        type: GET_DETAIL,
        payload: response.data,
    })
}
}
export const postRecipes = (form) => {
    return async (dispatch) => {
        var json = await axios.post("http://localhost:3001/recipe", form);
        return dispatch({
            type: POST_RECIPE,
            payload:json.data
        })
    }
}
