import {
    GET_RECIPES,
    GET_DIETS,
    SEARCH_RECIPES,
    GET_DETAIL,
    POST_RECIPE,
    FILTER_RECIPE_BY_DIETS,
    FILTER_RECIPE_BY_ORIGIN,
    SORT_BY_NAME,
    SORT_BY_HS,   
} from "./actionsTypes"


const initialState = {
    recipes: [],
    originalRecipes: [],
    recipeByID: {},
    diets: [],
    pepe: [],
}


export default function reducer(state = initialState, { type, payload }) {
    const allRecipes = state.originalRecipes
    switch (type) {
        case GET_RECIPES:
            return {
                ...state,
                recipes: payload,
                originalRecipes: payload
            }
        case GET_DIETS:
            return {
                ...state,
                diets: payload
            }
        case SEARCH_RECIPES:
            return {
                ...state,
                recipes: payload
            }
        case GET_DETAIL:
            return {
                ...state,
                recipes: payload
            }
        case POST_RECIPE:
            return {
                ...state,
                recipes: payload
            }
        case FILTER_RECIPE_BY_DIETS:
            const recipesFiltered = payload === 'all' ? allRecipes : allRecipes.filter(recipe => recipe.diets?.includes(payload));
            return {
                ...state,
                recipes: recipesFiltered
            }
        case FILTER_RECIPE_BY_ORIGIN:
            console.log("reducer", payload)
            const recipesByOrigin = payload === 'Creadas' ? allRecipes.filter(recipe => recipe.createdInDb) : allRecipes.filter(recipe => !recipe.createdInDb)
            return {
                ...state,
                recipes: payload === 'Todas' ? allRecipes : recipesByOrigin
            }
        case SORT_BY_NAME:
            const sortByName = payload === 'az'
                ?  allRecipes.sort((a, b) => {
                    if (a.name > b.name) {
                        return 1;
                    }
                    if (a.name < b.name) {
                        return -1;
                    }
                    return 0;
                }) :
                allRecipes.sort((a, b) => {
                    if (a.name > b.name) {
                        return -1;
                    }
                    if (a.name < b.name) {
                        return 1;
                    }
                    return 0;
                })
                
            return {
                ...state,
                recipes: payload === '-' ? allRecipes : sortByName.concat(arr)
            }
        case SORT_BY_HS:
            const sortByHs = payload === 'asc'
                ? allRecipes.sort((a, b) => {
                    if (a.healthScore > b.healthScore) {
                        return 1;
                    }
                    if (a.healthScore < b.healthScore) {
                        return -1;
                    }
                    return 0;
                }) :
                allRecipes.sort((a, b) => {
                    if (a.healthScore > b.healthScore) {
                        return -1;
                    }
                    if (a.healthScore < b.healthScore) {
                        return 1;
                    }
                    return 0;
                })
                
                
                return {
                    ...state,
                    recipes: payload === '-' ? allRecipes : sortByHs
                }

        default:
            return { ...state, }
    }
}
