import {GET_RECIPES, 
    GET_DIETS, 
    SEARCH_RECIPES,
    GET_DETAIL,  
    POST_RECIPE, 
    FILTER_RECIPE_BY_DIETS,
    FILTER_RECIPE_BY_ORIGIN, 
    SORT_BY_NAME, 
    SORT_BY_HS,
     } from "./actionsTypes"
import axios from 'axios'
import { getTokenFromLocalStorage } from './localStorageUtils.js';



export const getRecipes = () => {
    return async (dispatch) => {
        let response = await axios.get("http://localhost:3001/recipes")
        dispatch({
            type: GET_RECIPES,
            payload: response.data,
        })
    }
}

export const getDiets = () => {
    return async (dispatch) => {
    let response = await axios.get("http://localhost:3001/diets")
      dispatch({
        type: GET_DIETS,
        payload: response.data,
    })
}
}

export const searchRecipes = (name) => {
    return async (dispatch) =>{
    let response = await axios.get(`http://localhost:3001/recipes?name=${name}`)
     dispatch({
        type: SEARCH_RECIPES,
        payload: response.data,
    })
}
}

export const filterRecipeByDiets = (payload) => {
    return {
    type: FILTER_RECIPE_BY_DIETS,
    payload
}
}

export const filterRecipeByOrigin = (payload) => {
    return {
        type: FILTER_RECIPE_BY_ORIGIN,
        payload
    }
}
    
    
export const sortByName = (payload) => {
    return {
        type: SORT_BY_NAME,
        payload
    }
}
    export const sortByHs = (payload) => {
        return {
            type: SORT_BY_HS,
            payload
        }
    }
  

export const getDetail = (id) => {
    console.log("id",id)
    return async (dispatch) =>{
    let response = await axios.get(`http://localhost:3001/recipes/${id}`)
     dispatch({
        type: GET_DETAIL,
        payload: response.data,
    })
}
}

export const postRecipes = (form) => {
    return async (dispatch) => {
        const token = getTokenFromLocalStorage(); // función que devuelve el token JWT
        const config = {
            headers: { Authorization: `Bearer ${token}` }
        };
        const response = await axios.post("http://localhost:3001/recipe", form, config);
        dispatch({
            type: POST_RECIPE,
            payload: response.data
        });
    }
}
