import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDetail, cleanDetail } from "../../Redux/actions";
import { useParams } from "react-router-dom";
import style from "./Detail.module.css"

export default function Detail() {
  const dispatch = useDispatch();
  const recipeById = useSelector((state) => state.recipeByID);
  
  const { id } = useParams();

  useEffect(() => {
    dispatch(getDetail(id));
    return () => {
        dispatch(cleanDetail());
      };
  }, [dispatch, id]);

    if ((!Object.keys(recipeById).length)) {
    return <div className={style.loading}>
    <h1>CARGANDO...</h1>
  
    </div>
}
  return (
    
    <div className={style.container}>
      <>
        <p className={style.id}>Id: {recipeById.id}</p>
        <h1 className={style.name}>{recipeById.name}</h1>
        <img className={style.image} src={recipeById.image} alt="img not found" />
        <div className={style.summary} dangerouslySetInnerHTML={{ __html: recipeById.summary }} />
        <h2 className={style.healthScore}>Nivel de comida saludable</h2>
        <h3>{recipeById.healthScore}</h3>
        <div className={style.steps}>
        <h2>CÃ³mo hacerla</h2>
        <div>
          {recipeById.steps?.map((e, i) => {
            return <div key={i}>{i+1}- {e}</div>;
          })}
        </div>
        </div>
        <h2>Tipos de dietas</h2>
        <div className={style.diets}>
          {recipeById.Diets?.map((e, i) => {
            return <h4 key={i}>{e}</h4>;
          })}
        </div>
      </>
    </div>
  );
}
