import {GET_RECIPES, 
    GET_DIETS, 
    SEARCH_RECIPES,
    GET_DETAIL,  
    POST_RECIPE, 
    FILTER_RECIPE_BY_DIETS,
    FILTER_RECIPE_BY_ORIGIN, 
    SORT_BY_NAME, 
    SORT_BY_HS,
    LOADER } from "./actionsTypes"
import axios from 'axios'



export const getRecipes = () => {
    return async (dispatch) => {
        let response = await axios.get("http://localhost:3001/recipe")
        dispatch({
            type: GET_RECIPES,
            payload: response.data,
        })
    }
}


export const searchRecipes = (name) => {
    return async (dispatch) =>{
    let response = await axios(`http://localhost:3001/recipe?name=${name}`)
    return dispatch({
        type: SEARCH_RECIPES,
        payload: response.data,
    })
}
}

export const getDetail = (id) => {
    return async (dispatch) =>{
    let response = await axios(`http://localhost:3001/recipes/${id}`)
    return dispatch({
        type: GET_DETAIL,
        payload: response.data,
    })
}
}
export const postRecipes = (form) => {
    return async (dispatch) => {
        var json = await axios.post("http://localhost:3001/recipe", form);
        return dispatch({
            type: POST_RECIPE,
            payload:json.data
        })
    }
} 
