import style from "./Filters.module.css"
import { useSelector, useDispatch } from "react-redux"
import { filterRecipeByDiets } from "../../Redux/actions";

export default function Filters () {
    const dietas = useSelector(state => state.diets)
    const dispatch = useDispatch();

    const handleFilterDiet = (e) => {
        console.log()
        dispatch(filterRecipeByDiets(e.target.value)) 
    }

    return(
        <div className={style.filtros}>
        <p>Orden치 por nombre</p>
        <select title="Ordenar recetas por nombre">
            <option value="-">-</option>
            <option value="az">A-Z</option>
            <option value="za">Z-A</option>
        </select>
        <p>Orden치 por Healt Score</p>
        <select title="Ordenar recetas por Healt Score">
            <option value="-">-</option>
            <option value="asc">Ascendente</option>
            <option value="desc">Descendente</option>
        </select>
        <p>Filtr치 por dietas</p>
        <select title="Filtr치 por dietas" onChange={e => handleFilterDiet(e)}>
           <option value="all">Dietas</option>
           {dietas?.map((elem) => {
                            return (
                                <option value={elem.name}
                                 key={elem.id}
                                 >{elem.name}
                                 </option>)
                        })}
        </select>
    </div>
    )
}
