import React, { useState, useEffect } from "react";
import { postRecipes, getDiets } from "../../Redux/actions";
import { useDispatch, useSelector } from "react-redux";

export default function Form() {
  const dispatch = useDispatch();
  const diets = useSelector((state) => state.diets);

  const [input, setInput] = useState({
    name: "",
    image: "",
    summary: "",
    healthScore: "",
    steps: [],
    diets: [],
  });

console.log(input)

  const handleChange = (e) => {
    setInput({
      ...input,
      [e.target.name]: e.target.value,
    });
  };

  const handleSelect = (e) => {
    setInput({
      ...input,
      diets: [...input.diets, e.target.value],
    });
  };

  const handleResetdiets = () => {
    setInput({
      ...input,
      diets: [],
    });
  };

  const handleSubmit = (e) => {
    e.prevent.default();
    console.log(input)
    dispatch(postRecipes(input))
    alert("Dieta subida... A cocinar!!")
    set
  }

  useEffect(() => {
    dispatch(getDiets());
  }, [dispatch]);

  return (
    <div>
      <h1>Cre√° tu receta</h1>
      <form
        action="submit-form.php"
        method="post"
        enctype="multipart/form-data"
      >
        <label for="nombre">Nombre:</label>
        <input
          type="text"
          id="nombre"
          name="name"
          value={input.name}
          onChange={handleChange}
          required
        />
        <br />

        <label for="resumen">Resumen del plato:</label>
        <textarea
          id="resumen"
          name="summary"
          rows="4"
          cols="50"
          value={input.summary}
          onChange={handleChange}
          required
        ></textarea>
        <br />

        <label for="saludable">Nivel de comida saludable:</label>
        <input
          type="number"
          id="saludable"
          name="healthScore"
          min="0"
          max="100"
          value={input.healthScore}
          onChange={handleChange}
          required
        />
        <br />

        <label for="pasoapaso">Paso a paso:</label>
        <textarea
          id="pasoapaso"
          name="steps"
          rows="8"
          cols="50"
          value={input.steps}
          onChange={handleChange}
          required
        ></textarea>
        <br />

        <label for="imagen">Imagen:</label>
        <input
          type="file"
          id="imagen"
          name="image"
          value={input.image}
          onChange={handleChange}
          required
        />
        <br />

        <select onChange={handleSelect}>
          {diets?.map((elem) => {
            return (
              <option value={elem.name} key={elem.id}>
                {elem.name}
              </option>
            );
          })}
        </select>
        <button type="button" onClick={handleResetdiets}>
          reset dietas
        </button>
        <br />
        <>
          {input.diets.map((elem) => (
            <h4>{elem}</h4>
          ))}
        </>
        <input type="submit" value="Crear Receta" />
      </form>
    </div>
  );
}
