import React from "react"
import { useEffect } from "react"
import style from "./Home.module.css"
import Card from "../Card/card"
import { getRecipes, getDiets } from "../../Redux/actions"
import { useDispatch, useSelector } from "react-redux"

 

export default function Home () {
   const dispatch = useDispatch(); 
   const dietas = useSelector(state => state.diets)
    const recetas = useSelector(state => state.recipes)

    useEffect(()=>{
        dispatch(getRecipes())
        dispatch(getDiets())
    },  [dispatch])
   
    console.log(recetas[0])
   console.log(dietas)
   
   return(
        <div>
            <div className="">
                <p>Ordenar por nombre</p>
                <select title="Ordenar recetas por nombre">
                    <option value="-">-</option>
                    <option value="asc">Ascendente</option>
                    <option value="desc">Descendente</option>
                </select>
                <p>Ordenar por Healt Score</p>
                <select title="Ordenar recetas por Healt Score">
                    <option value="-">-</option>
                    <option value="asc">Ascendente</option>
                    <option value="desc">Descendente</option>
                </select>
                <p>Filtrar por recetas creadas o de la página</p>
                <select title= "Elegir recetas creadas o de la página">
                    <option value="-">-</option>
                    <option value="api">Nuestras Recetas</option>
                    <option value="bdd">Creadas por el usuario</option>
                </select>
                
                 

            </div>
         <div className={style.cardsContent} >
             {recetas?.map(e => {
                        return (
                            <Card 
                             id={e.id}
                             name={e.name} 
                             image={e.image}
                             diets={e.diets}
                             key={e.id}
                             healthscore={e.healthscore} />
                        )
                    })}  
           </div>
        </div>
    )
}
