import React, { useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { getDetail } from "../../Redux/actions";
import { useParams } from "react-router-dom";


export default function Detail () {
    
    const dispatch = useDispatch();
    
    const { id } = useParams();
    console.log(id)
    
    useEffect(()=>{
        dispatch(getDetail(id));
    },[dispatch, id])

    const recipeById = useSelector(state => state.recipeById);
    console.log()
    
    return(
        <div>
            {recipeById && (
                <>
                    <p>{recipeById.id}</p>
                    <h1>{recipeById.name}</h1>
                    <img src={recipeById.image} alt="img not found"/>
                    <p>{recipeById.summary}</p>
                    <h3>{recipeById.healthscore}</h3>
                    <p>{recipeById.steps}</p>
                    <div>
                        {recipeById.diets?.map((e, i) =>{ 
                            return(
                            <h3 key={i}>{e}</h3>)
                        })}
                    </div>
                </>
            )}
        </div>
    )
}
