import {GET_RECIPES, 
        GET_DIETS, 
        SEARCH_RECIPES,
        GET_DETAIL,  
        POST_RECIPE, 
        FILTER_RECIPE_BY_DIETS,
        FILTER_RECIPE_BY_ORIGIN, 
        SORT_BY_NAME, 
        SORT_BY_HS,
        LOADER } from "./actionsTypes"


const initialState = {
    recipes: [],
    recipeByID: {},
    diets:[],
    loader: true,
}


export default function reducer(state = initialState, {type, payload}) {
    switch (type) {
        case GET_RECIPES:
            return {
                ...state,
                recipes: payload
            }
            case GET_DIETS:
                return {
                    ...state,
                    diets: payload
                }
                case SEARCH_RECIPES:
            return {
                ...state,
                recipes: payload
            }
            case GET_DETAIL:
            return {
                ...state,
                recipes: payload
            }
            case POST_RECIPE:
            return {
                ...state,
                recipes: payload
            }
            case FILTER_RECIPE_BY_DIETS:
                const allRecipes = state.recipes
                const recipesFiltered = payload === 'all' ? allRecipes : allRecipes.filter(rec => r.diets?.some((d) => d === payload))
            return {
                ...state,
                recipes: recipesFiltered
            }
            case FILTER_RECIPE_BY_ORIGIN:
            return {
                ...state,
                recipes: payload
            }
            case SORT_BY_NAME:
            return {
                ...state,
                recipes: payload
            }
            case SORT_BY_HS:
            return {
                ...state,
                recipes: payload
            }
            case LOADER:
            return {
                ...state,
                recipes: payload
            }
            default:
            return { ...state, }
        }
    }
